object JsonParser extends App {
  import Implicits._
  private[this] val parser = new JawnParser

  private def writeToCsv(filename: String, lines: Seq[CSVLine]) = {
    val name = s"$filename.csv"
    val tempFile = new java.io.File(name)
    println(tempFile.toPath)
    val writer = new CSVWriter(new FileWriter(tempFile, Charset.forName("UTF-8")))
    val headers1 =
      List[String]("offer_id", "offer_name", "product_id", "product_name", "currency", "amount", "discount_id",
        "discountamount")
    writer.writeNext(headers1.toArray, false)
    lines.foreach { l =>
      val values = Array[String](
        l.offerId,
        l.offerName,
        l.productId,
        l.productName,
        l.currency,
        l.amount.toString,
        l.discountId,
        l.discountAmount.toString)
      writer.writeNext(values, false)
    }
    writer.close()
  }
